import{_ as a,c as t,o as s,ag as r,j as e,a as i}from"./chunks/framework.i4hb9nkZ.js";const o="/Qonnectra-docs/pr-3/images/manual/teil-a/conduit_connection.jpg",d="/Qonnectra-docs/pr-3/images/manual/teil-a/conduit_connection_editArea.jpg",u="/Qonnectra-docs/pr-3/images/manual/teil-a/conduit_connection_routing.jpg",h="/Qonnectra-docs/pr-3/images/manual/teil-a/conduit_connection_showTrench.jpg",l="/Qonnectra-docs/pr-3/videos/conduit_connection_showTrench.webm",g="/Qonnectra-docs/pr-3/images/manual/teil-a/conduit_connection_conduitSelection.jpg",c="/Qonnectra-docs/pr-3/images/manual/teil-a/conduit_connection_conduit.jpg",m="/Qonnectra-docs/pr-3/videos/conduit_connection_mapSelection.webm",p="/Qonnectra-docs/pr-3/videos/conduit_connection_mapFind.webm",R=JSON.parse('{"title":"7. Rohrzuordnung","description":"","frontmatter":{},"headers":[],"relativePath":"manual/teil-a-anwenderhandbuch/07-rohrzuordnung.md","filePath":"manual/teil-a-anwenderhandbuch/07-rohrzuordnung.md"}'),b={name:"manual/teil-a-anwenderhandbuch/07-rohrzuordnung.md"};function z(_,n,k,w,f,v){return s(),t("div",null,[...n[0]||(n[0]=[r('<h1 id="_7-rohrzuordnung" tabindex="-1">7. Rohrzuordnung <a class="header-anchor" href="#_7-rohrzuordnung" aria-label="Permalink to &quot;7. Rohrzuordnung&quot;">​</a></h1><p>Die <strong>Rohrzuordnung</strong> dient ausschließlich der Zuordnung von Rohren zu bestehenden <strong>Trassenabschnitten (Trassensegmenten)</strong>. Ziel ist es, zu dokumentieren, welche Rohre auf welchen Trassenverläufen liegen. Die Rohrzuordnung ist damit ein zentrales Werkzeug zur strukturellen Verknüpfung von Netzbestandteilen – nicht zur allgemeinen Kartennavigation.</p><p><img src="'+o+'" alt="Screenshot Rohrzurdnung Übersicht" style="display:block;max-width:512px;width:100%;margin:0 auto;"></p><h2 id="_7-1-grundprinzip" tabindex="-1">7.1 Grundprinzip <a class="header-anchor" href="#_7-1-grundprinzip" aria-label="Permalink to &quot;7.1 Grundprinzip&quot;">​</a></h2><p>Solange kein Rohr ausgewählt ist, kann nichts in der Karte selektiert werden. Die Karte zeigt Elemente wie Trassen, Rohrabzweige, Hausanschlüsse und Adresspunkte an, diese sind jedoch nur zur Orientierung sichtbar. Die Karte dient hier ausschließlich der Zuordnung von Rohren zu Trassen.</p><h2 id="_7-2-kartennavigation-und-modi" tabindex="-1">7.2 Kartennavigation und Modi <a class="header-anchor" href="#_7-2-kartennavigation-und-modi" aria-label="Permalink to &quot;7.2 Kartennavigation und Modi&quot;">​</a></h2><p>Die Kartenfunktionen entsprechen vollständig denen in der allgemeinen Karte (siehe Kapitel <a href="./05-karte.html">Karte</a>):</p><ul><li>Suchen über das Suchfeld (oben links)</li><li>Transparenz anpassen (unten links)</li><li>Ein- und Ausblenden von Layern über die interaktive Legende (oben rechts)</li></ul><p>Rechts neben der Karte befindet sich der Bearbeitungsbereich mit verschiedenen Auswahlmöglichkeiten und einer Liste der zugeordneten Trassensegmente zu einem ausgewählten Rohr.</p><p><img src="'+d+'" alt="Screenshot Rohrzuordnung mit Hervorhebung des Bearbeitungsbereichs rechts" style="display:block;max-width:512px;width:100%;margin:0 auto;"></p><h3 id="_7-2-1-routing-modus" tabindex="-1">7.2.1 Routing-Modus <a class="header-anchor" href="#_7-2-1-routing-modus" aria-label="Permalink to &quot;7.2.1 Routing-Modus&quot;">​</a></h3><p>Der Routing-Modus bestimmt, wie die Zuordnung von Rohr zu Trasse beim Klicken auf Trassensegmente in der Karte vollzogen wird.</p><p><img src="'+u+'" alt="Screenshot Rohrverwaltung Arbeitsbereich mit Hervorhebung der Schaltfläche für den Routing-Modus" style="display:block;max-width:512px;width:100%;margin:0 auto;"></p><ul><li><p>Routing-Modus <strong>ausgeschaltet</strong> (Regler links und grau): Einzelne Trassenabschnitte können per Klick ausgewählt und dem Rohr hinzugefügt werden. Hierbei wird immer nur ein Trassensegment der Liste hinzugefügt.</p></li><li><p>Routing-Modus <strong>eingeschaltet</strong> (Regler rechts und farbig): Wählen Sie zuerst ein Trassensegment als Start- und dann eins als Endpunkt aus (2 Klicks in der Karte). Das System berechnet automatisch den besten Pfad zwischen beiden Punkten und ordnet die entsprechenden Trassensegmente dem Rohr zu. Hierbei werden mehrere Segmente der Liste auf der rechten Seite hinzugefügt.</p></li></ul><h3 id="_7-2-2-trassenverbindungen-anzeigen" tabindex="-1">7.2.2 Trassenverbindungen anzeigen <a class="header-anchor" href="#_7-2-2-trassenverbindungen-anzeigen" aria-label="Permalink to &quot;7.2.2 Trassenverbindungen anzeigen&quot;">​</a></h3><p>Beim Umschalter &quot;Trassenverbindungen anzeigen&quot; haben Sie die Möglichkeit bei einer Rohrauswahl, eine visuelle Rückmeldung über bereits bestehende Zuordnungen zu Segmenten in der Karte zu erhalten.</p><p><img src="'+h+'" alt="Screenshot Rohrverwaltung Arbeitsbereich mit Hervorhebung der Schaltfläche Trassenverbindungen anzeigen" style="display:block;max-width:512px;width:100%;margin:0 auto;"></p><ul><li><p>Trassenverbindungen anzeigen <strong>ausgeschaltet</strong> (Regler links und grau): Es erfolgt keine visuelle Hervorhebung in der Karte.</p></li><li><p>Trassenverbindungen <strong>eingeschaltet</strong> (Regler rechts und farbig): Beim Auswählen eines Rohrs werden alle zugehörigen Trassenabschnitte in der Karte farblich hervorgehoben.</p></li></ul>',18),e("p",null,[e("video",{controls:"",playsinline:"",preload:"metadata",style:{"max-width":"100%","border-radius":"12px"},src:l})],-1),r('<h2 id="_7-3-zuordnung-von-rohren-zu-trassensegmenten" tabindex="-1">7.3 Zuordnung von Rohren zu Trassensegmenten <a class="header-anchor" href="#_7-3-zuordnung-von-rohren-zu-trassensegmenten" aria-label="Permalink to &quot;7.3 Zuordnung von Rohren zu Trassensegmenten&quot;">​</a></h2><p>Mit Auswahl eines Rohrs wird automatisch der Bearbeitungsmodus aktiviert, in dem Zuordnungen direkt übernommen werden – ohne Speichern-Button. Änderungen werden sofort gespeichert und können nicht rückgängig gemacht werden.</p><h3 id="_7-3-1-auswahl-von-rohren" tabindex="-1">7.3.1 Auswahl von Rohren <a class="header-anchor" href="#_7-3-1-auswahl-von-rohren" aria-label="Permalink to &quot;7.3.1 Auswahl von Rohren&quot;">​</a></h3><p>Die verfügbaren Rohre richten sich nach dem oben ausgewählten Projekt (oben links in der Kopfzeile) und dem eingestellten Kennzeichen (direkt über der Rohrauswahl).</p><p><img src="'+g+'" alt="Screenshot mit Hervorhebung Projektauswahl und Kennzeichenauswahl" style="display:block;max-width:512px;width:100%;margin:0 auto;"></p><p>Zur Auswahl eines Kennzeichens klicken Sie auf den &quot;Pfeil-nach-unten&quot; neben dem Eingabefeld oder tippen Sie in das Eingabefeld. Danach öffnet sich eine Liste, aus der Sie das entsprechende Kennzeichen wählen können.</p><p>Die Rohrauswahl im Feld darunter funktioniert ebenso. Zur Auswahl eines Rohrs klicken Sie auf den &quot;Pfeil-nach-unten&quot; neben dem Eingabefeld oder tippen Sie in das Eingabefeld. Danach öffnet sich eine Liste, aus der Sie das entsprechende Rohr wählen können.</p><p><img src="'+c+'" alt="Screenshot Bearbeitungsbereich mit Hervorhebung Eingabefeld Rohr" style="display:block;max-width:512px;width:100%;margin:0 auto;"></p><p>Ist das ausgewählte Rohr bereits Trassensegmenten auf der Karte zugeordnet, erscheint anschließend darunter eine Liste mit Einträgen zu Trassensegmenten (Bezeichnung: Trassen-ID).</p><h3 id="_7-3-2-rohre-trassensegementen-zuordnen" tabindex="-1">7.3.2 Rohre Trassensegementen zuordnen <a class="header-anchor" href="#_7-3-2-rohre-trassensegementen-zuordnen" aria-label="Permalink to &quot;7.3.2 Rohre Trassensegementen zuordnen&quot;">​</a></h3><p>Die Rohre werden den Trassensegmenten anhand der Auswahl in der Karte hinzugefügt. Hierzu klicken Sie auf ein Trassensegment in der Karte. Beachten Sie, ob die Trassen in der Karte eingeschaltet sind (Layer in der Legende über das Auge-Symbol einschalten). Bitte beachten Sie auch, ob der Routing-Modus an (zwei Klicks auf der Karte für Start- und Endpunkt) oder aus (ein Klick für ein Trassensegment) geschaltet ist. Zum besseren Verständnis finden Sie die Erklärung zum Routing-Modus weiter oben in diesem Abschnitt.</p><p>Wenn die Zuordnung erfolgreich war, erscheint am unteren Bildschirmrand eine kurze Bestätigungsnachricht. Gleichzeitig wird das Trassensegment in der Liste unterhalb des ausgewählten Rohrs angezeigt.</p>',12),e("p",null,[e("video",{controls:"",playsinline:"",preload:"metadata",style:{"max-width":"100%","border-radius":"12px"},src:m})],-1),e("p",null,'Sollte "Trassenverbindungen anzeigen" (weiter oben) aktiviert sein, wird das hinzugefügte Trassensegment dauerhaft farblich hervorgehoben.',-1),e("h3",{id:"_7-3-3-zugeordnete-trassensegmente-finden",tabindex:"-1"},[i("7.3.3 Zugeordnete Trassensegmente finden "),e("a",{class:"header-anchor",href:"#_7-3-3-zugeordnete-trassensegmente-finden","aria-label":'Permalink to "7.3.3 Zugeordnete Trassensegmente finden"'},"​")],-1),e("p",null,"Wenn Sie in der Liste auf eine Trassen-ID klicken, wird die Karte zu diesem Segment gezoomt und dieses kurz blinkend hervorgehoben. Bitte beachten Sie, dass die Liste aus mehreren Seiten bestehen kann. Am unteren Ende der Liste finden Sie eine Möglichkeit zum Umblättern.",-1),e("p",null,[e("video",{controls:"",playsinline:"",preload:"metadata",style:{"max-width":"100%","border-radius":"12px"},src:p})],-1),e("h3",{id:"_7-3-4-loschen-einer-zuordnung",tabindex:"-1"},[i("7.3.4 Löschen einer Zuordnung "),e("a",{class:"header-anchor",href:"#_7-3-4-loschen-einer-zuordnung","aria-label":'Permalink to "7.3.4 Löschen einer Zuordnung"'},"​")],-1),e("p",null,"Falls einer Trasse versehentlich ein Rohr zugeordnet wurde, kann diese Zuordnung über das Papierkorb-Symbol neben der Trassen-ID in der Liste wieder gelöscht werden. Um die Zuordnung kontrolliert vorzunehmen, hilft es, über die Legende rechts die Labels (Beschriftungen) der Trassensegmente zu aktivieren. Diese zeigen die Trassen-IDs an, die Sie mit der Liste abgleichen können.",-1)])])}const S=a(b,[["render",z]]);export{R as __pageData,S as default};
